import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getProductsBySlug } from "../../actions";
import Layout from "../../components/Layout";
import "./style.css";

export default function ProductListPage(props) {
	const dispatch = useDispatch();
	const product = useSelector((state) => state.product);

	useEffect(() => {
		const { match } = props;

		dispatch(getProductsBySlug(match.params.slug));
	}, []);

	return (
		<Layout>
			{Object.keys(product.productByPrice).map((key, index) => {
				return (
					<div className='card'>
						<div className='cardHeader'>
							<div>Samsung Mobile Under 10k</div>
							<button>view All</button>
						</div>
						<div>
                            {
                                pr
                            }

							<div className='productContainer'>
								<div className='productImageContainer'>
									<img
										src='http://localhost:2000/public/QlUTB7pYrV-m31-2--.jpeg'
										alt='Samsung M31'
									/>
								</div>
								<div className='productInfo'>
									<div className='productName'>Samsung Galaxy M31</div>
									<div>
										<span>4.5</span>&nbsp;
										<span>46367</span>
									</div>
									<div className='productPrice'>15000</div>
								</div>
							</div>
						</div>
					</div>
				);
			})}
		</Layout>
	);
}
