import React, { useState, useEffect } from "react";
import Layout from "../../components/Layout";
import { useSelector, useDispatch } from "react-redux";
import {
	addToCart,
	decreaseCart,
	getCartItems,
} from "../../actions/cart.action";
import Card from "../../components/UI/Card";
import CartItem from "./CartItem";
import "./style.css";
import CartItem2 from "./CartItem/index3";
import { MaterialButton } from "../../components/MaterialUI";
import PriceDetails from "../../components/PriceDetails";

export default function Cart2(props) {
	const cart = useSelector((state) => state.cart);
	const auth = useSelector((state) => state.auth);
	const dispatch = useDispatch();
	// const cartItems = cart.cartItems;
	const [cartItems, setCartItems] = useState(cart.cartItems);

	useEffect(() => {
		setCartItems(cart.cartItems);
	}, [cart.cartItems]);

	useEffect(() => {
		if (auth.authenticate) {
			dispatch(getCartItems());
		}
	}, [auth.authenticate]);

	const onQuantityIncrement = (_id, qty) => {
		const { name, price, img } = cartItems[_id];

		dispatch(addToCart({ _id, name, price, img }, 1));
	};

	const onQuantityDecrement = (_id, qty) => {
		const { name, price, img } = cartItems[_id];

		dispatch(addToCart({ _id, name, price, img }, -1));
	};
	if (props.onlyCartItems) {
		return (
			<>
				{" "}
				{Object.keys(cartItems).map((key, index) => (
					<CartItem
						key={index}
						cartItem={cartItems[key]}
						onQuantityInc={onQuantityIncrement}
						onQuantityDec={onQuantityDecrement}
					/>
				))}
			</>
		);
	}

	return (
		<Layout>
			<div className='container-fluid'>
				<div className='row'>
					<div className='col-md-10 col-11 mx-auto'>
						<div className='row mt-5 gx-3'>
							<div className='col-md-12 col-lg-8 col-11 mx-auto main_cart mb-lg-0 mb-5 shadow'>
								{Object.keys(cartItems).map((key, index) => (
									<CartItem2
										key={index}
										cartItem={cartItems[key]}
										onQuantityInc={onQuantityIncrement}
										onQuantityDec={onQuantityDecrement}
									/>
								))}
								<hr />
							</div>
							<div className='col-md-12 col-lg-4 col-11 mx-auto mt-lg-0 mt-md-5'>
								<div className='right_side p-3 shadow bg-white'>
									<h2 className='product_name mb-5'>
										Total Item:
										{Object.keys(cart.cartItems).reduce(function (qty, key) {
											return qty + cart.cartItems[key].qty;
										}, 0)}
									</h2>
									<div className='price_indiv d-flex justify-content-between'>
										<p>Product amount</p>
										<p>
											$
											<span id='product_total_amt'>
												{Object.keys(cart.cartItems).reduce(
													(totalPrice, key) => {
														const { price, qty } = cart.cartItems[key];
														return totalPrice + price * qty;
													},
													0
												)}
											</span>
										</p>
									</div>
									<div className='price_indiv d-flex justify-content-between'>
										<p>Shipping Charge</p>
										<p>
											$<span id='shipping_charge'>50.0</span>
										</p>
									</div>
									<hr />
									<div className='total-amt d-flex justify-content-between font-weight-bold'>
										<p>The total amount of (including VAT)</p>
										<p>
											$
											<span id='total_cart_amt'>
												{Object.keys(cart.cartItems).reduce(
													(totalPrice, key) => {
														const { price, qty } = cart.cartItems[key];
														return totalPrice + price * qty;
													},
													0
												)}
											</span>
										</p>
									</div>
									<button className='btn btn-primary text-uppercase'>
										Checkout
									</button>
								</div>
								<div className='discount_code mt-3 shadow'>
									<div className='card'>
										<div className='card-body'>
											<a
												className='d-flex justify-content-between'
												data-toggle='collapse'
												href='#collapseExample'
												aria-expanded='false'
												aria-controls='collapseExample'>
												Add a discount code (optional)
												<span>
													<i className='fas fa-chevron-down pt-1'></i>
												</span>
											</a>
											<div className='collapse' id='collapseExample'>
												<div className='mt-3'>
													<input
														type='text'
														name=''
														id='discount_code1'
														className='form-control font-weight-bold'
														placeholder='Enter the discount code'
													/>
													<small id='error_trw' className='text-dark mt-3'>
														code is thapa
													</small>
												</div>
												<button
													className='btn btn-primary btn-sm mt-3'
													onclick='discount_code()'>
													Apply
												</button>
											</div>
										</div>
									</div>
								</div>

								<div className='mt-3 shadow p-3 bg-white'>
									<div className='pt-4'>
										<h5 className='mb-4'>Expected delivery date</h5>
										<p>July 27th 2020 - July 29th 2020</p>
									</div>
								</div>
							</div>
						</div>
					</div>
				</div>
			</div>
		</Layout>
	);
}
