import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { Link } from "react-router-dom";
import Radio from "@material-ui/core/Radio";
import RadioGroup from "@material-ui/core/RadioGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import FormControl from "@material-ui/core/FormControl";
import FormLabel from "@material-ui/core/FormLabel";
import { addRecharge, getRecharges } from "../../actions/recharge.action";
import { StylesProvider } from "@material-ui/styles";

export default function Recharge() {
	const [number, setNumber] = useState("");
	const [amount, setAmount] = useState(null);
	const [type, setType] = useState("");
	const [provider, setProvider] = useState("");
	const dispatch = useDispatch();

	const userRecharge = (e) => {
		e.preventDefault()
		console.log('Submit button clicked')
		dispatch(addRecharge({ number, amount, type, provider }));
		console.log({ number, amount, type, provider });
		dispatch(getRecharges())
	};

	useEffect(() => {

	dispatch(getRe)
	}, [input])


	return (
		<div className='login-container'>
			<div className='icon-container'></div>
			<form>
				<h2>Recharge</h2>

				<div className='input-container'>
					<label className='label'>Enter Your Phone number</label>
					<br />
					<input
						className='input'
						type='text'
						value={number}
						placeholder='Enter your phone'
						onChange={(e) => setNumber(e.target.value)}
					/>
				</div>

				<div className='input-container'>
					<label className='label'>Enter recharge Amount</label>
					<br />
					<input
						className='input'
						type='text'
						style={{ width: "80px" }}
						placeholder='BDT'
						onChange={(e) => setAmount(e.target.value)}
					/>
				</div>
				<div>
					<FormControl component='fieldset'>
						<FormLabel component='legend'>
							<h4>Sim type</h4>
						</FormLabel>
						<RadioGroup
							aria-label='gender'
							name='gender1'
							value={type}
							onChange={(e) => setType(e.target.value)}>
							<FormControlLabel
								value='prepaid'
								control={<Radio />}
								label='Prepaid'
							/>
							<FormControlLabel
								value='postpaid'
								control={<Radio />}
								label='Postpaid'
							/>
						</RadioGroup>
					</FormControl>
				</div>

				<FormControl component='fieldset'>
					<FormLabel component='legend'>
						<h4> Provider</h4>
					</FormLabel>
					<RadioGroup
						aria-label='provider'
						name='gender1'
						value={provider}
						onChange={(e) => setProvider(e.target.value)}>
						<FormControlLabel
							value='grameenphone'
							control={<Radio />}
							label='Grameen Phone'
						/>
						<FormControlLabel
							value='banglalink'
							control={<Radio />}
							label='Banglalink'
						/>
						<FormControlLabel
							value='airtel'
							control={<Radio />}
							label='Airtel'
						/>
						<FormControlLabel value='robi' control={<Radio />} label='Robi' />
						<FormControlLabel
							value='teletalk'
							control={<Radio />}
							label='TeleTalk'
						/>
					</RadioGroup>
				</FormControl>

				<div className='btn-container'>
					<button className='submit-btn' onClick={userRecharge}>
						Submit
					</button>
				</div>
			</form>
		</div>
	);
}
