import { cartConstants } from "../actions/constants";

const initState = {
	cartItems: {
		// 123:{
		//     _id:123,
		//     name:'',
		//     img:'',
		//     price:'',
		//     qty:''
		// }
	},
	up
};

export default (state = initState, action) => {
	switch (action.type) {
		case cartConstants.Add_TO_CART_REQUEST:
			state = {
				...state,
				updatingCart:true
			};
			break;
	}
	return state;
};
