import React, { useState,useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import CssBaseline from "@material-ui/core/CssBaseline";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Paper from "@material-ui/core/Paper";
import Stepper from "@material-ui/core/Stepper";
import Step from "@material-ui/core/Step";
import StepLabel from "@material-ui/core/StepLabel";
import Link from "@material-ui/core/Link";
import Review from "./Review";
import { useSelector, useDispatch } from "react-redux";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import TextField from "@material-ui/core/TextField";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Checkbox from "@material-ui/core/Checkbox";
import Radio from "@material-ui/core/Radio";
import RadioGroup from "@material-ui/core/RadioGroup";
import FormControl from "@material-ui/core/FormControl";
import { addAddress,getAddress ,addOrder} from "../../../actions/user.action";
import {getCartItems} from '../../../actions/cart.action'
import "./style.css";
import Swal from "sweetalert2";
import { Button, SwipeableDrawer } from "@material-ui/core";
import "./style.css";

function Copyright() {
	return (
		<Typography variant='body2' color='textSecondary' align='center'>
			{"Copyright Â© "}
			<Link color='inherit' href='https://material-ui.com/'>
				Your Website
			</Link>{" "}
			{new Date().getFullYear()}
			{"."}
		</Typography>
	);
}

const useStyles = makeStyles((theme) => ({
	appBar: {
		position: "relative",
	},
	layout: {
		width: "auto",
		fontSize: "1.8 rem",
		marginLeft: theme.spacing(2),
		marginRight: theme.spacing(2),
		[theme.breakpoints.up(600 + theme.spacing(2) * 2)]: {
			width: 600,
			marginLeft: "auto",
			marginRight: "auto",
		},
	},
	paper: {
		marginTop: theme.spacing(3),
		marginBottom: theme.spacing(3),
		padding: theme.spacing(2),
		[theme.breakpoints.up(600 + theme.spacing(3) * 2)]: {
			marginTop: theme.spacing(6),
			marginBottom: theme.spacing(6),
			padding: theme.spacing(3),
		},
	},
	stepper: {
		padding: theme.spacing(3, 0, 5),
	},
	buttons: {
		display: "flex",
		justifyContent: "flex-end",
	},
	button: {
		marginTop: theme.spacing(3),
		marginLeft: theme.spacing(1),
	},
}));



export default function Checkout(props) {
	const steps = ["Shipping address", "Review your order", " Payment details"];
	const classes = useStyles();
	const [activeStep, setActiveStep] = React.useState(0);
	const dispatch = useDispatch()
	const user = useSelector(state => state.user)
	const auth = useSelector((state) => state.auth);
		const cart = useSelector((state) => state.cart);

	const handleNext = () => {
		setActiveStep(activeStep + 1);
	};

	const handleBack = () => {
		setActiveStep(activeStep - 1);
	};

	//Address from Hooks............
	const previousAddress = user.address;
	const oldAddress = user.address.slice(user.address.length - 1)[0];
	console.log(oldAddress);
	useEffect(() => {
		dispatch(getAddress());
	}, []);
	const { initialData, onCancel } = props;
	const [name, setName] = useState(null)
	const [mobileNumber, setMobileNumber] = useState(
		oldAddress ? oldAddress.mobileNumber : ""
	);
	const [zipCode, setZipCode] = useState(oldAddress ? oldAddress.zipCode : "");

	const [address, setAddress] = useState(oldAddress ? oldAddress.address : "");
	const [cityDistrict, setCityDistrict] = useState(
		oldAddress ? oldAddress.cityDistrict : ""
	);

	const [alternatePhone, setAlternatePhone] = useState(
		oldAddress ? oldAddress.alternatePhone : ""
	);
	console.log(name);

	const selectedAddress = user.address.slice(user.address.length - 1)[0];
	console.log(oldAddress);
	const [id, setId] = useState(initialData ? initialData._id : "");

//Order confirm hooksssss............
		const [confirmOrder, setConfirmOrder] = useState('')
		const onConfirmOrder = () => {
			const totalAmount = Object.keys(cart.cartItems).reduce(
				(totalPrice, key) => {
					const { price, qty } = cart.cartItems[key];
					return totalPrice + price * qty;
				},
				0
			);
			const items = Object.keys(cart.cartItems).map((key) => ({
				productId: key,
				payablePrice: cart.cartItems[key].price,
				purchasedQty: cart.cartItems[key].qty,
			}));
			const payload = {
				addressId: selectedAddress._id,
				totalAmount,
				items,
				paymentStatus: "pending",
				paymentType: "cod",
			};

			console.log(payload);
			dispatch(addOrder(payload));
			setConfirmOrder(true);
		};


 useEffect(() => {

	 dispatch(getAddress())
 }, [])

	useEffect(() => {
		auth.authenticate && dispatch(getAddress());
		auth.authenticate && dispatch(getCartItems());
	}, [auth.authenticate]);


function getStepContent(activeStep) {
	switch (activeStep) {
		case 0:
			return <AddressForm />;
		case 1:
			return  <Review />;
		case 2:
			return <PaymentForm/>;
		default:
			throw new Error("Unknown step");
	}
}



 //Address form............
	const onAddressSubmit = (e) => {
		const payload = {
			address: {
				name,
				mobileNumber,
				zipCode,
				address,
				cityDistrict,
				alternatePhone,
			},
		};
		console.log(payload);
		if (id) {
			payload.address._id = id;
		}
		if (name || mobileNumber || zipCode || address || cityDistrict == "") {
			console.log(payload);
			// return Swal.fire({
			// 	icon: "error",
			// 	title: "Oops...",
			// 	text: "Submit form correctly..!",
			// });
			console.log(selectedAddress);
		}

		dispatch(addAddress(payload));
		setActiveStep(activeStep + 1);
	};


 function AddressForm(props) {


		return (
			<React.Fragment>
				<div className='mainContainer' style={{ fontSize: "1.8rem" }}>
					<Typography variant='h6' gutterBottom>
						<h4>Shipping address</h4>
					</Typography>

					 <div class="content">
      <form action="#">
        <div class="user-details">
          <div class="input-box">
            <span class="details">Full Name</span>
            <input type="text" placeholder="Enter your name" required onChange={(e)=>setName(e.target.val)}/>
          </div>
          <div class="input-box">
            <span class="details">Username</span>
            <input type="text" placeholder="Enter your username" required/>
          </div>
          <div class="input-box">
            <span class="details">Email</span>
            <input type="text" placeholder="Enter your email" required/>
          </div>
          <div class="input-box">
            <span class="details">Phone Number</span>
            <input type="text" placeholder="Enter your number" required/>
          </div>
          <div class="input-box">
            <span class="details">Password</span>
            <input type="text" placeholder="Enter your password" required/>
          </div>
          <div class="input-box">
            <span class="details">Confirm Password</span>
            <input type="text" placeholder="Confirm your password" required/>
          </div>
        </div>
        <div class="gender-details">
          <input type="radio" name="gender" id="dot-1"/>
          <input type="radio" name="gender" id="dot-2"/>
          <input type="radio" name="gender" id="dot-3"/>
          <span class="gender-title">Gender</span>
          <div class="category">
            <label for="dot-1">
            <span class="dot one"></span>
            <span class="gender">Male</span>
          </label>
          <label for="dot-2">
            <span class="dot two"></span>
            <span class="gender">Female</span>
          </label>
          <label for="dot-3">
            <span class="dot three"></span>
            <span class="gender">Prefer not to say</span>
            </label>
          </div>
        </div>
        <div class="button">
          <input type="submit" value="Register"/>
        </div>
      </form>
    </div>
					<Button
						fullWidth
						style={{ backgroundColor: "yellow", color: "gray" }}
						onClick={onAddressSubmit}>
						<h5>Submit Address</h5>
					</Button>
				</div>
			</React.Fragment>
		);
 }


	function PaymentForm() {
		const [paymentType, setPaymentType] = useState("");
		return (
			<React.Fragment>
				<Typography variant='h6' gutterBottom>
					Payment method
				</Typography>
				<Grid container spacing={3}>
					<FormControl component='fieldset'>
						{/* <FormLabel component='legend'>Payment Type</FormLabel> */}
						<RadioGroup
							aria-label='Payment Type'
							name='gender1'
							value={paymentType}
							onChange={(e) => setPaymentType(e.target.value)}>
							<FormControlLabel
								value='Bkash'
								control={<Radio />}
								label='Bkash'
							/>
							<FormControlLabel
								value='ssl'
								control={<Radio />}
								label='SSL Commerce'
							/>
							<FormControlLabel
								value='cod'
								control={<Radio />}
								label='Cash On Delivery'
							/>
						</RadioGroup>
					</FormControl>
					<Button fullWidth  style={{color:'black',backgroundColor:'yellow'}} onClick={onConfirmOrder}>Confirm Order</Button>
				</Grid>
			</React.Fragment>
		);
	}

	return (
		<React.Fragment>
			<div className='mainContainer'>
				{/* <CssBaseline /> */}
				<AppBar position='absolute' color='default' className={classes.appBar}>
					<Toolbar>
						<Typography variant='h6' color='inherit' noWrap>
							Company name
						</Typography>
					</Toolbar>
				</AppBar>
				<main className={classes.layout}>
					<Paper className={classes.paper}>
						<Typography component='h1' variant='h4' align='center'>
							<h3>Checkout</h3>
						</Typography>
						<Stepper activeStep={activeStep} className={classes.stepper}>
							{steps.map((label) => (
								<Step key={label}>
									<StepLabel>{label}</StepLabel>
								</Step>
							))}
						</Stepper>
						<React.Fragment>
							{activeStep === steps.length ? (
								<React.Fragment>
									<Typography variant='h5' gutterBottom>
										Thank you for your order.
									</Typography>
									<Typography variant='subtitle1'>
										Your order number is #2001539. We have emailed your order
										confirmation, and will send you an update when your order
										has shipped.
									</Typography>
								</React.Fragment>
							) : (
								<React.Fragment>
									{getStepContent(activeStep)}
									<div className={classes.buttons}>
										{activeStep !== 0 && (
											<Button onClick={handleBack} className={classes.button}>
												Back
											</Button>
										)}
										<Button
											variant='contained'
											color='primary'
											onClick={handleNext}
											className={classes.button}>
											{activeStep === steps.length - 1 ? "Place order" : "Next"}
										</Button>
									</div>
								</React.Fragment>
							)}
						</React.Fragment>
					</Paper>
					<Copyright />
				</main>
			</div>
		</React.Fragment>
	);
}
