import React from 'react'
import './style2.css'

export default function Register(props) {


	const [phone, setPhone] = useState("");
	const [name, setName] = useState("");
	const [email, setEmail] = useState("");
	const [password, setPassword] = useState("");
	const [confirmPassword, setConfirmPassword] = useState("");
	const dispatch = useDispatch();

	const auth = useSelector((state) => state.auth);

	const userRegister = () => {
		if (password != confirmPassword) {
			return new Noty({
				text: "Password Didn't match ",
			}).show();
		}
		dispatch(register({ name, email, phone, password }));
	};

	useEffect(() => {
		if (auth.authenticate) return <Redirect to='/' />;
	}, [auth.authenticate]);
	if (auth.authenticate) return <Redirect to='/' />;
	return (
		<div>
			<div className='login-container'>
				<div className='icon-container'></div>
				<form>
					<h2>Register</h2>
					<div className='input-container'>
						<label>Enter Your Full name</label>
						<br />
						<input className='input' type='text' placeholder='Your Full Name' />
					</div>
					<div className='input-container'>
						<label>Enter Your Email</label>
						<br />
						<input className='input' type='email' placeholder='Enter your email' />
					</div>
					<div className='input-container'>
						<label>Enter Your Phone number</label>
						<br />
						<input className='input' type='text' placeholder='Enter your phone' />
					</div>
					<div className='input-container'>
						<label>Password</label>
						<br />
						<input className='input' type='password' placeholder='Your password' />
					</div>
					<div className='input-container'>
						<label>Confirm Password</label>
						<br />
						<input
							className='input'
							type='password'
							placeholder='Confirm password'
						/>
					</div>

					<div className='btn-container'>
						<button className='submit-btn'>Register</button>
					</div>
					<h2>OR</h2>
					<p>Login With</p>
					<div className='loginicon-container'>
						<div className='icon'>
							<img
								className='iconImg'
								src='https://img-authors.flaticon.com/google.jpg'
							/>
						</div>
						<div className='icon'>
							<img
								className='iconImg'
								src='https://1000logos.net/wp-content/uploads/2016/11/Facebook-logo.png'
							/>
						</div>
					</div>
				</form>
			</div>
		</div>
	);
}
