import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getProductDetailsById } from "../../actions";
import Layout from "../../components/Layout";
import { IoIosArrowForward, IoIosStar, IoMdCart } from "react-icons/io";
import { BiRupee } from "react-icons/bi";
import { AiFillThunderbolt } from "react-icons/ai";
import { MaterialButton } from "../../components/MaterialUI";
import "./style2.css";
import { generatePublicUrl } from "../../urlconfig.js";
import { addToCart } from "../../actions/cart.action";

export default function ProductDetails2(props) {
      const dispatch = useDispatch();
			const product = useSelector((state) => state.product);

			useEffect(() => {
				const { productId } = props.match.params;
				console.log(props);
				const payload = {
					params: {
						productId,
					},
				};
				dispatch(getProductDetailsById(payload));
			}, []);

			if (Object.keys(product.productDetails).length === 0) {
				return null;
			}
    return (
			<Layout>
				<section className='section product-detail'>
					<div className='details container'>
						<div className='left'>
							<div className='main'>
								<img
									src={generatePublicUrl(
										product.productDetails.productPictures[0].img
									)}
									alt={`${product.productDetails.productPictures[0].img}`}
									alt=''
								/>
							</div>
							<div className='thumbnails'>
								{product.productDetails.productPictures.map((thumb, index) => (
									<div className='thumbnail'>
										<img src={generatePublicUrl(thumb.img)} alt={thumb.img} />
									</div>
								))}
								{/* <div className='thumbnail'>
									<img src='./images/product2.jpg' alt='' />
								</div>
								<div className='thumbnail'>
									<img src='./images/product3.jpg' alt='' />
								</div>
								<div className='thumbnail'>
									<img src='./images/product4.jpg' alt='' />
								</div>
								<div className='thumbnail'>
									<img src='./images/product5.jpg' alt='' />
								</div> */}
							</div>
						</div>
						<div className='right'>
							<span>Home/T-shirt</span>
							<h3>{product.productDetails.name}</h3>
							<div className='price'>${product.productDetails.price}</div>
							<form>
								<div>
									<select>
										<option value='Select Size' selected disabled>
											Select Size
										</option>
										<option value='1'>32</option>
										<option value='2'>42</option>
										<option value='3'>52</option>
										<option value='4'>62</option>
									</select>
									<span>
										<i className='fas fa-chevron-down'></i>
									</span>
								</div>
							</form>

							<form className='form'>
								<input type='text' placeholder='1' />
								<a

									onClick={() => {
										const { _id, name, price } = product.productDetails;
										const img = product.productDetails.productPictures[0].img;
										dispatch(addToCart({ _id, name, price, img }));
										props.history.push("/cart");
									}}
									className='addCart'>
									Add To Cart
								</a>
							</form>
							<h3>Product Detail</h3>
							<p>{product.productDetails.desc}</p>
						</div>
					</div>
				</section>
			</Layout>
		);
}
