import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getProductDetailsById } from "../../actions";
import Layout from "../../components/Layout";
import { IoIosArrowForward, IoIosStar, IoMdCart } from "react-icons/io";
import { BiRupee } from "react-icons/bi";
import { AiFillThunderbolt } from "react-icons/ai";
import { MaterialButton } from "../../components/MaterialUI";
import "./style2.css";
import { generatePublicUrl } from "../../urlconfig.js";
import { addToCart } from "../../actions/cart.action";
import { Redirect } from "react-router";

import PropTypes from "prop-types";
import { makeStyles } from "@material-ui/core/styles";
import AppBar from "@material-ui/core/AppBar";
import Tabs from "@material-ui/core/Tabs";
import Tab from "@material-ui/core/Tab";
import Typography from "@material-ui/core/Typography";
import Box from "@material-ui/core/Box";

function TabPanel(props) {
	const { children, value, index, ...other } = props;

	return (
		<div
			role='tabpanel'
			hidden={value !== index}
			id={`simple-tabpanel-${index}`}
			aria-labelledby={`simple-tab-${index}`}
			{...other}>
			{value === index && (
				<Box p={3}>
					<Typography>{children}</Typography>
				</Box>
			)}
		</div>
	);
}

TabPanel.propTypes = {
	children: PropTypes.node,
	index: PropTypes.any.isRequired,
	value: PropTypes.any.isRequired,
};

function a11yProps(index) {
	return {
		id: `simple-tab-${index}`,
		"aria-controls": `simple-tabpanel-${index}`,
	};
}

const useStyles = makeStyles((theme) => ({
	root: {
		flexGrow: 1,
		backgroundColor: theme.palette.background.paper,
	},
}));




export default function ProductDetails2(props) {
	 const classes = useStyles();
		const [value, setValue] = React.useState(0);

		const handleChange = (event, newValue) => {
			setValue(newValue);
		};












      const dispatch = useDispatch();
			const product = useSelector((state) => state.product);

			useEffect(() => {
				const { productId } = props.match.params;
				console.log(props);
				const payload = {
					params: {
						productId,
					},
				};
				dispatch(getProductDetailsById(payload));
			}, []);

			if (Object.keys(product.productDetails).length === 0) {
				return null;
			}
    return (
			<Layout>
				<section className='section product-detail m-0 p-1'>
					<div className='details container'>
						<div className='left'>
							<div className='main'>
								<img
									src={generatePublicUrl(
										product.productDetails.productPictures[0].img
									)}
									alt={`${product.productDetails.productPictures[0].img}`}
									alt=''
								/>
							</div>
							<div className='thumbnails'>
								{product.productDetails.productPictures.map((thumb, index) => (
									<div className='thumbnail'>
										<img src={generatePublicUrl(thumb.img)} alt={thumb.img} />
									</div>
								))}
								{/* <div className='thumbnail'>
									<img src='./images/product2.jpg' alt='' />
								</div>
								<div className='thumbnail'>
									<img src='./images/product3.jpg' alt='' />
								</div>
								<div className='thumbnail'>
									<img src='./images/product4.jpg' alt='' />
								</div>
								<div className='thumbnail'>
									<img src='./images/product5.jpg' alt='' />
								</div> */}
							</div>
						</div>
						<div className='right'>
							<span>Home/T-shirt</span>
							<h3>{product.productDetails.name}</h3>
							<div className='price'>${product.productDetails.price}</div>
							<form>
								<div>
									<select>
										<option value='Select Size' selected disabled>
											Select Size
										</option>
										<option value='1'>32</option>
										<option value='2'>42</option>
										<option value='3'>52</option>
										<option value='4'>62</option>
									</select>
									<span>
										<i className='fas fa-chevron-down'></i>
									</span>
								</div>
							</form>

							<form className='form'>
								<input type='text' placeholder='1' />
								<a
									style={{ backgroundColor: "fce00d" }}
									onClick={() => {
										const { _id, name, price } = product.productDetails;
										const img = product.productDetails.productPictures[0].img;
										dispatch(addToCart({ _id, name, price, img }));
										<Redirect to='/cart' />;
										// props.history.push("/cart");
									}}
									className='addToCart'>
									Add To Cart
								</a>
							</form>
							<h3>Product Detail</h3>
							<p>{product.productDetails.desc}</p>
						</div>
					</div>
				</section>

				<div>
					<div className={classes.root}>
						<AppBar position='static'>
							<Tabs
								value={value}
								onChange={handleChange}
								aria-label='simple tabs example'>
								<Tab label='Item One' {...a11yProps(0)} />
								<Tab label='Item Two' {...a11yProps(1)} />
								<Tab label='Item Three' {...a11yProps(2)} />
							</Tabs>
						</AppBar>
						<TabPanel value={value} index={0}>
							Item One
						</TabPanel>
						<TabPanel value={value} index={1}>
							Item Two
						</TabPanel>
						<TabPanel value={value} index={2}>
							Item Three
						</TabPanel>
					</div>
				</div>
			</Layout>
		);
}
