				speed: 500,
				slidesToShow: 6,
				slidesToScroll: 2,
				autoplay: true,
				speed: 4000,
				autoplaySpeed: 4000,
				pauseOnHover: true,

				responsive: [
					{
						breakpoint: 1024,
						settings: {
							slidesToShow: 4,
							slidesToScroll: 1,
							infinite: true,
							dots: true,
						},
					},
					{
						breakpoint: 600,
						settings: {
							slidesToShow: 4,
							slidesToScroll: 1,
							initialSlide: 3,
						},
					},
					{
						breakpoint: 580,
						settings: {
							slidesToShow: 3,
							slidesToScroll: 1,
						},
					},
				],
			};


			// const renderCategories = (categories) => {
			// 	let myCategories = [];

			// 	for (let category of categories) {
			// 		myCategories.push(
			// 			<li key={category.name}>
			// 				{category.parentId ? (
			// 					<a
			// 						href={`/${category.slug}?cid=${category._id}&type=${category.type}`}>
			// 						{category.name}
			// 					</a>
			// 				) : (
			// 					<span>{category.name}</span>
			// 				)}

			// 				{category.children ? (
			// 					<ul>{renderCategories(category.children)}</ul>
			// 				) : null}
			// 			</li>
			// 		);
			// 	}
			// 	return myCategories;
			// };
            const myCategories = [];
            categories.map((category,index)=>{

             category.children.map((child,index)=>{
                 myCategories.push(child)
             })

            })

            console.log(myCategories)


	return (
		// <Grid container className={classes.root} spacing={2}>
		// 	<Grid item xs={12}>
		<Slider {...settings}>
			{/* <Grid container justify='center' spacing={spacing}> */}
			{myCategories.map((category) => (
				<Link
					to={`/${category.slug}?cid=${category._id}&type=${category.type}`}
					style={{ textDecoration: "none" }}>
					<div
						className='categorybox'
						style={{
							display: "flex",
							flexDirection: "column",
						}}>
						<img
							style={{
								width: "100px",
								height: "80px",
								borderRadius: "10px",
							}}
							src={domain+$`${category.categoryIma}`}
						/>
						<h4 style={{ margin: "auto" }}>{category.name}</h4>
					</div>
				</Link>
			))}
			{/* </Grid> */}
		</Slider>
		// 	</Grid>
		// </Grid>
	);
}
