import "./App.css";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import Header from "./components/Header";
import HomePage from "./containers/HomePage";
import ProductListPage from "./containers/ProductListPage";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import { isUserLoggedIn } from "./actions/auth.action";
import ProductDetailsPage from "./containers/ProductDetailsPage";
import ProductDetailsPage2 from "./containers/ProductDetailsPage/index2";
import CartPage from "./containers/CartPage";
import CartPage2 from "./containers/CartPage/index2";
import CheckoutPage from "./containers/CheckoutPage";
import LoginRegister from "./containers/Login_Register";
import Login from "./containers/Login";
import Register from "./containers/Register";
import CheckoutPage2 from "./containers/CheckoutPage/option2/Checkout";
import { updateCart } from "./actions/cart.action";
import OrderPage from "./containers/OrderPage";
import OrderDetailsPage from "./containers/OrderDetailsPage";
import CartPage3 from "./containers/CartPage/index3";
function App() {
	const dispatch = useDispatch();
	const auth = useSelector((state) => state.auth);

	useEffect(() => {
		if (!auth.authenticate) {
			dispatch(isUserLoggedIn());
		}
	}, [auth.authenticate]);

	useEffect(() => {
		dispatch(updateCart());
	}, [auth.authenticate]);
	return (
		<div className='App'>
			<Router>
				<Switch>
					<Route path='/' exact component={HomePage} />
					<Route path='/cart' component={CartPage3} />
					<Route path='/checkout' component={CheckoutPage2} />
					<Route path='/login_register' component={LoginRegister} />
					<Route path='/login' component={LoginRegister} />
					<Route path='/register' component={Register} />
					<Route path='/account/orders' component={OrderPage} />
					<Route path='/order_details/:orderId' component={OrderDetailsPage} />

					<Route
						path='/:productSlug/:productId/p'
						component={ProductDetailsPage2}
					/>
					<Route path='/:slug' component={ProductListPage} />
				</Switch>
			</Router>
		</div>
	);
}

export default App;
