import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getOrder } from "../../actions";
import Card from "../../components/UI/Card";
import Price from "../../components/UI/Price/Price";
import { generatePublicUrl } from "../../urlconfig";
import item from "../HomePage/item";
import "./style2.css";
import './style.css'

export default function OrderDetailsPage(props) {
	const dispatch = useDispatch();
	const orderDetails = useSelector((state) => state.user.orderDetails);
	const orderId = props.match.params.orderId;

	useEffect(() => {
		// console.log({ props });
		const payload = {
			orderId: orderId,
		};
		dispatch(getOrder(payload));
	}, []);

	const formatDate = (date) => {
		if (date) {
			const d = new Date(date);
			return `${d.getFullYear()}-${d.getMonth() + 1}-${d.getDate()}`;
		}
		return "";
	};

	const formatDate2 = (date) => {
		const month = [
			"Jan",
			"Feb",
			"Mar",
			"Apr",
			"May",
			"June",
			"July",
			"Aug",
			"Sep",
			"Oct",
			"Nov",
			"Dec",
		];
		if (date) {
			const d = new Date(date);
			return `${month[d.getMonth()]} ${d.getDate()}, ${d.getFullYear()}`;
		}
	};

	if (!(orderDetails && orderDetails.address)) {
		return null;
	}
	return (
		<div className='container'>
			<article className='card'>
				<header className='card-header'> My Orders / Tracking </header>
				<div className='card-body'>
					<h6>Order ID:{orderDetails._id} </h6>
					<article className='card'>
						<div className='card-body row'>
							<div className='col'>
								{" "}
								<strong>Estimated Delivery time:</strong> <bd />
								29 nov 2019{" "}
							</div>
							<div className='col'>
								{" "}
								<strong>Address:</strong> <bd />
								{orderDetails.address.address},{" "}
								{orderDetails.address.cityDistrict}
								{/* | <i className='fa fa-phone'></i>{" "}+1598675986{" "} */}
							</div>
							<div className='col'>
								{" "}
								<strong>Status:</strong> <bd /> Picked by the courier{" "}
							</div>
							<div className='col'>
								{" "}
								<strong>Tracking #:</strong> <bd /> BD045903594059{" "}
							</div>
						</div>
					</article>
					<div className='track'>
						<div className='step active'>
							{" "}
							<span className='icon'>
								{" "}
								<i className='fa fa-check'></i>{" "}
							</span>{" "}
							<span className='text'>Order confirmed</span>{" "}
						</div>
						<div className='step active'>
							{" "}
							<span className='icon'>
								{" "}
								<i className='fa fa-user'></i>{" "}
							</span>{" "}
							<span className='text'> Picked by courier</span>{" "}
						</div>
						<div className='step'>
							{" "}
							<span className='icon'>
								{" "}
								<i className='fa fa-truck'></i>{" "}
							</span>{" "}
							<span className='text'> On the way </span>{" "}
						</div>
						<div className='step'>
							{" "}
							<span className='icon'>
								{" "}
								<i className='fa fa-box'></i>{" "}
							</span>{" "}
							<span className='text'>Ready for pickup</span>{" "}
						</div>
					</div>
					<hr />
					<ul className='row'>
						{/* {orderDetails.items.map(item => {
							<li className='col-md-4'>
								<figure className='itemside mb-3'>
									<div className='aside'>
										{console.log(item.productId.productPictures)}
										<img src='' className='img-sm border' />
									</div>
									<figcaption className='info align-self-center'>
										<p className='title'>
											{console.log(item.productId.name)}
											{item.productId.name} <bd /> 8GB RAM
										</p>
										<span className='text-muted'>
											{item.productId.payablePrice}
										</span>
									</figcaption>
								</figure>
							</li>;
						})} */}
						{orderDetails.items.map((item, index) => (
							<Card
								style={{
									display: "flex",
									padding: "20px 0",
									margin: "10px 0",
								}}>
								<div className='flexRow'>
									<div className='delItemImgContainer'>
										<img
											src={generatePublicUrl(
												item.productId.productPictures[0].img
											)}
											alt=''
										/>
									</div>
									<div style={{ width: "250px" }}>
										<div className='delItemName'>{item.productId.name}</div>
										<Price value={item.payablePrice} />
									</div>
								</div>
								<div style={{ padding: "25px 50px" }}>
									<div className='orderTrack'>
										{orderDetails.orderStatus.map((status) => (
											<div
												className={`orderStatus ${
													status.isCompleted ? "active" : ""
												}`}>
												<div
													className={`point ${
														status.isCompleted ? "active" : ""
													}`}></div>
												<div className='orderInfo'>
													<div className='status'>{status.type}</div>
													<div className='date'>{formatDate(status.date)}</div>
												</div>
											</div>
										))}
									</div>
								</div>
								<div style={{ fontWeight: "500", fontSize: 14 }}>
									{orderDetails.orderStatus[3].isCompleted &&
										`Delivered on ${formatDate2(
											orderDetails.orderStatus[3].date
										)}`}
								</div>
							</Card>
						))}
					</ul>
					<hr />
					<a href='#' className='btn btn-warning' data-abc='true'>
						{" "}
						<i className='fa fa-chevron-left'></i> Back to orders
					</a>
				</div>
			</article>
		</div>
	);
}
