import React, { useState, useEffect } from "react";

import { useDispatch, useSelector } from "react-redux";

import { Container, Card, Row, Col } from "react-bootstrap";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import FormLabel from "@material-ui/core/FormLabel";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import RadioGroup from "@material-ui/core/RadioGroup";
import Radio from "@material-ui/core/Radio";
import ShoppingCartIcon from "@material-ui/icons/ShoppingCart";
import Paper from "@material-ui/core/Paper";
import { Link } from "react-router-dom";
import { addToCart } from "../../actions/cart.action";
import "./style.css";
import { api, generatePublicUrl } from "../../urlconfig";

export default function NowPlaying({ movie }) {

	const product = useSelector((state) => state.product);
	var settings = {
		dots: false,
		infinite: true,
		speed: 500,
		slidesToShow: 4,
		slidesToScroll: 1,
	};
	return (
		<Container>
			<div className='clearfix mt-5 mb-2'>
				<h4 className='float-left'>Now Playing</h4>
				<Link className='float-right text-uppercase' to='/'>
					see all
				</Link>
			</div>
			<Slider {...settings}>
				{product.products.map(function (product) {
					return (
						<Grid key={index} item>
							<div className='paperContainer'>
								<Link to={`/${product.slug}/${product._id}/p`}>
									<Paper
										className={classes.paper}
										style={{ display: "flex", flexDirection: "column" }}>
										<div style={{ backgroundColor: "#dbdbdb" }}>
											<img
												className='productImg'
												src={generatePublicUrl(
													product.productPictures[0]
														? product.productPictures[0].img
														: null
												)}
											/>
											<p style={{ color: "#cf9415", fontWeight: "bold" }}>
												<span>৳</span> {product.price}
											</p>
											<p style={{ fontSize: "13px" }}>
												{product.name.slice(0, 20)}
											</p>
										</div>

										<div>
											<span className='cartIconContainer'>
												<ShoppingCartIcon
													onClick={() => {
														const { _id, name, price } = product;
														const img = product.productPictures[0].img;
														dispatch(addToCart({ _id, name, price, img }));
														props.history.push("/cart");
													}}
													style={{ fontSize: "30" }}
													className='cartIcon'
												/>
											</span>
										</div>
									</Paper>
								</Link>
							</div>
						</Grid>
					);
				})}
			</Slider>
		</Container>
	);
}
