import React, { Component } from "react";
import Slider from "react-slick";

export default function TopSlider() {
	const settings = {
		dots: true,
		infinite: true,
		speed: 500,
		slidesToShow: 1,
		autoplay: true,
		speed: 2000,
		autoplaySpeed: 2000,
		slidesToScroll: 1,
	};
	return (
		<div>
			<h2> Single Item</h2>
			<Slider {...settings}>
				<div>
					<img src='https://i.pinimg.com/originals/f7/f2/a2/f7f2a27aa9d45b73d6b52a40eabb50ae.png' />
				</div>
				<div>
					<img src='https://i.pinimg.com/originals/31/83/3a/31833a25051d8871ae2e0511d796fc85.png' />
				</div>
				<div>
					<img src='https://ak.picdn.net/shutterstock/videos/1036421099/thumb/4.jpg' />
				</div>
				<div>
					<img src='https://i.pinimg.com/originals/31/83/3a/31833a25051d8871ae2e0511d796fc85.png' />
				</div>

			</Slider>
		</div>
	);
}
