import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
	getAllCategory,
	getProductsBySlug,
	getSubCategory,
} from "../../../actions";
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import FormLabel from "@material-ui/core/FormLabel";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import RadioGroup from "@material-ui/core/RadioGroup";
import Radio from "@material-ui/core/Radio";
import Paper from "@material-ui/core/Paper";
import { Link } from "react-router-dom";
import "./style.css";
import { generatePublicUrl } from "../../../urlconfig";


const useStyles = makeStyles((theme) => ({
	root: {
		flexGrow: 1,
	},
	paper: {
		height: 180,
		width: 150,
	},
	control: {
		padding: theme.spacing(2),
	},
}));

export default function ProductStore(props) {

	const search=props.location.search
	const parentId = search.slice(5, 29);

	const [spacing, setSpacing] = React.useState(2);
	const classes = useStyles();

	const handleChange = (event) => {
		setSpacing(Number(event.target.value));
	};

	const dispatch = useDispatch();
	const products = useSelector((state) => state.product);

	const [priceRange, setPriceRange] = useState({
		under5k: "5000",
		under10k: "10000",
		under15k: "15000",
		under20k: "20000",
		under30k: "30000",
	});
	const { match } = props;
	console.log(match.params.slug);
	const category = useSelector((state) => state.category);
	const subCategories=category.subCategories
	const categories = category.categories;
	const categoriesArray = [];

	// Object.keys(categories).map((cat,index)=>{
	// 	categoriesArray.push(cat)
	// })
	// console.log(categoriesArray)
	// const selectedCategory = categories.filter((category) => {
	// 	return category.slug == match.params.slug;
	// });

	// console.log(selectedCategory);

	useEffect(() => {
		const { match } = props;
		console.log(match);

		dispatch(getAllCategory());
		dispatch(getProductsBySlug(match.params.slug));
		dispatch(getSubCategory(parentId));
	}, []);

	return (
		<>
			<div>
				{subCategories ? (
					<Grid container className={classes.root} spacing={2}>
						<Grid item xs={12}>
							<Grid container justify='center' spacing={spacing}>
								{subCategories.map((category) => (
									<div className='mx-sm-1 mx-lg-4'>
										<Link
											className='categoryLink'
											to={`/${category.slug}?cid=${category._id}&type=${category.type}`}>
											<div
												className='categorybox'
												style={{
													display: "flex",
													flexDirection: "column",
												}}>
												<img
													style={{
														width: "100px",
														height: "80px",
														borderRadius: "10px",
													}}
													src='https://cdn.mos.cms.futurecdn.net/vEcELHdn998wRTcCzqV5m9.jpg'
												/>
												<h4 style={{ margin: "auto" }}>{category.name}</h4>
											</div>
										</Link>
									</div>
								))}
							</Grid>
						</Grid>
					</Grid>
				) : null}

				<Grid container className={classes.root} spacing={2}>
					<Grid item xs={12}>
						<Grid container justify='center' spacing={spacing}>
							{products.map((product, index) => (
								<Grid key={index} item>
									<Paper className={classes.paper} />
									<img src={generatePublicUrl(product.product)} />
									<div>{product.pri}</div>
								</Grid>
							))}
						</Grid>
					</Grid>
				</Grid>
			</div>
		</>
	);
}
