import React, { useState ,useEffect} from "react";
import { useDispatch } from "react-redux";
import { Link } from "react-router-dom";
import Radio from "@material-ui/core/Radio";
import RadioGroup from "@material-ui/core/RadioGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import FormControl from "@material-ui/core/FormControl";
import FormLabel from "@material-ui/core/FormLabel";
import { addRecharge, getRecharges } from "../../actions/recharge.action";
import Footer from "../../components/Footer";

export default function Recharge() {
	const [number, setNumber] = useState("");
	const [amount, setAmount] = useState(null);
	const [type, setType] = useState("");
	const [provider, setProvider] = useState("");
	const dispatch = useDispatch();

	const userRecharge = (e) => {
		e.preventDefault()
		console.log('Submit button clicked')
		dispatch(addRecharge({ number, amount, type, provider }));
		console.log({ number, amount, type, provider });
		dispatch(getRecharges())
	};

	useEffect(() => {

	dispatch(getRecharges())
	}, [])


	return (
		<>
			<div className='login-container'>
				<div className='icon-container'></div>
				<form>
					<h2>Recharge</h2>

					<div className='input-container'>
						<label className='label'>Enter Your Phone number</label>
						<br />
						<input
							className='input'
							type='text'
							value={number}
							placeholder='Enter your phone'
							onChange={(e) => setNumber(e.target.value)}
						/>
					</div>

					<div className='input-container'>
						<label className='label'>Enter recharge Amount</label>
						<br />
						<input
							className='input'
							type='text'
							style={{ width: "80px" }}
							placeholder='BDT'
							onChange={(e) => setAmount(e.target.value)}
						/>
					</div>
					<div>
						<FormControl component='fieldset'>
							<FormLabel component='legend'>
								<h4>Sim type</h4>
							</FormLabel>
							<RadioGroup
								aria-label='gender'
								name='gender1'
								value={type}
								onChange={(e) => setType(e.target.value)}>
								<FormControlLabel
									value='prepaid'
									control={<Radio />}
									label='Prepaid'
								/>
								<FormControlLabel
									value='postpaid'
									control={<Radio />}
									label='Postpaid'
								/>
							</RadioGroup>
						</FormControl>
					</div>

					<FormControl component='fieldset'>
						<FormLabel component='legend'>
							<h4> Provider</h4>
						</FormLabel>
						<RadioGroup
							aria-label='provider'
							name='gender1'
							value={provider}
							onChange={(e) => setProvider(e.target.value)}>
							<div style={{display:'flex',flexDirection:'c'}}>
								<img
									style={{ width: "20px", height: "20px" }}
									src='https://img.utdstc.com/icon/0a7/a25/0a7a257a5f5ce669a0a9c3f79a2b950e7956fa7974a83052dabcfe771b668211:200'
								/>
								<FormControlLabel
									value='grameenphone'
									control={<Radio />}
									label='Grameen Phone'
								/>
							</div>
							<div>
								<img src />
								<FormControlLabel
									value='banglalink'
									control={<Radio />}
									label='Banglalink'
								/>
							</div>
							<div>
								<img/>
								<FormControlLabel
									value='airtel'
									control={<Radio />}
									label='Airtel'
								/>
							</div>
							<FormControlLabel value='robi' control={<Radio />} label='Robi' />
							<FormControlLabel
								value='teletalk'
								control={<Radio />}
								label='TeleTalk'
							/>
						</RadioGroup>
					</FormControl>

					<div className='btn-container'>
						<button className='submit-btn' onClick={userRecharge}>
							Submit
						</button>
					</div>
				</form>
			</div>
			<Footer />
		</>
	);
}
