import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import CssBaseline from "@material-ui/core/CssBaseline";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Paper from "@material-ui/core/Paper";
import Stepper from "@material-ui/core/Stepper";
import Step from "@material-ui/core/Step";
import StepLabel from "@material-ui/core/StepLabel";
import Link from "@material-ui/core/Link";
import AddressForm from "./AddressForm";
import PaymentForm from "./PaymentForm";
import Review from "./Review";
import { useSelector, useDispatch } from "react-redux";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import TextField from "@material-ui/core/TextField";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Checkbox from "@material-ui/core/Checkbox";
import { addAddress } from "../../../actions/user.action";
import "./style.css";
import Swal from "sweetalert2";
import { Button, SwipeableDrawer } from "@material-ui/core";
import "./style.css";

function Copyright() {
	return (
		<Typography variant='body2' color='textSecondary' align='center'>
			{"Copyright © "}
			<Link color='inherit' href='https://material-ui.com/'>
				Your Website
			</Link>{" "}
			{new Date().getFullYear()}
			{"."}
		</Typography>
	);
}

const useStyles = makeStyles((theme) => ({
	appBar: {
		position: "relative",
	},
	layout: {
		width: "auto",
		fontSize: "1.8 rem",
		marginLeft: theme.spacing(2),
		marginRight: theme.spacing(2),
		[theme.breakpoints.up(600 + theme.spacing(2) * 2)]: {
			width: 600,
			marginLeft: "auto",
			marginRight: "auto",
		},
	},
	paper: {
		marginTop: theme.spacing(3),
		marginBottom: theme.spacing(3),
		padding: theme.spacing(2),
		[theme.breakpoints.up(600 + theme.spacing(3) * 2)]: {
			marginTop: theme.spacing(6),
			marginBottom: theme.spacing(6),
			padding: theme.spacing(3),
		},
	},
	stepper: {
		padding: theme.spacing(3, 0, 5),
	},
	buttons: {
		display: "flex",
		justifyContent: "flex-end",
	},
	button: {
		marginTop: theme.spacing(3),
		marginLeft: theme.spacing(1),
	},
}));

const steps = ["Shipping address", "Payment details", "Review your order"];

function getStepContent(step) {
	switch (step) {
		case 0:
			return renderAddressForm();
		case 1:
			return <PaymentForm />;
		case 2:
			return <Review />;
		default:
			throw new Error("Unknown step");
	}
}

export default function Checkout(props) {
	const classes = useStyles();
	const [activeStep, setActiveStep] = React.useState(0);

	const handleNext = () => {
		setActiveStep(activeStep + 1);
	};

	const handleBack = () => {
		setActiveStep(activeStep - 1);
	};



	// Config for Address form

		const { initialData, onCancel } = props;
		const [name, setName] = useState(initialData ? initialData.name : "");
		const [mobileNumber, setMobileNumber] = useState(
			initialData ? initialData.mobileNumber : ""
		);
		const [zipCode, setZipCode] = useState(
			initialData ? initialData.pinCode : ""
		);
		const [locality, setLocality] = useState(
			initialData ? initialData.locality : ""
		);
		const [address, setAddress] = useState(
			initialData ? initialData.address : ""
		);
		const [cityDistrict, setCityDistrict] = useState(
			initialData ? initialData.cityDistrictTown : ""
		);
		const [state, setState] = useState(initialData ? initialData.state : "");
		const [landmark, setLandmark] = useState(
			initialData ? initialData.landmark : ""
		);
		const [alternatePhone, setAlternatePhone] = useState(
			initialData ? initialData.alternatePhone : ""
		);
		const [addressType, setAddressType] = useState(
			initialData ? initialData.addressType : ""
		);
		const dispatch = useDispatch();
		const user = useSelector((state) => state.user);

		const [submitFlag, setSubmitFlag] = useState(false);
		const [id, setId] = useState(initialData ? initialData._id : "");

		const onAddressSubmit = (e) => {
			const payload = {
				address: {
					name,
					mobileNumber,
					zipCode,
					address,
					cityDistrict,
					alternatePhone,
				},
			};
			console.log(payload);
			if (id) {
				payload.address._id = id;
			}
			if (name || mobileNumber || zipCode || address || cityDistrict == "") {
				return Swal.fire({
					icon: "error",
					title: "Oops...",
					text: "Submit form correctly..!",
				});
			}
			dispatch(addAddress(payload));
			setSubmitFlag(true);
		};


	return (
		<React.Fragment>
			<div className='mainContainer'>
				{/* <CssBaseline /> */}
				<AppBar position='absolute' color='default' className={classes.appBar}>
					<Toolbar>
						<Typography variant='h6' color='inherit' noWrap>
							Company name
						</Typography>
					</Toolbar>
				</AppBar>
				<main className={classes.layout}>
					<Paper className={classes.paper}>
						<Typography component='h1' variant='h4' align='center'>
							<h3>Checkout</h3>
						</Typography>
						<Stepper activeStep={activeStep} className={classes.stepper}>
							{steps.map((label) => (
								<Step key={label}>
									<StepLabel>{label}</StepLabel>
								</Step>
							))}
						</Stepper>
						<React.Fragment>
							{activeStep === steps.length ? (
								<React.Fragment>
									<Typography variant='h5' gutterBottom>
										Thank you for your order.
									</Typography>
									<Typography variant='subtitle1'>
										Your order number is #2001539. We have emailed your order
										confirmation, and will send you an update when your order
										has shipped.
									</Typography>
								</React.Fragment>
							) : (
								<React.Fragment>
									{getStepContent(activeStep)}
									<div className={classes.buttons}>
										{activeStep !== 0 && (
											<Button onClick={handleBack} className={classes.button}>
												Back
											</Button>
										)}
										<Button
											variant='contained'
											color='primary'
											onClick={handleNext}
											className={classes.button}>
											{activeStep === steps.length - 1 ? "Place order" : "Next"}
										</Button>
									</div>
								</React.Fragment>
							)}
						</React.Fragment>
					</Paper>
					<Copyright />
				</main>
			</div>
		</React.Fragment>
	);
}
