import axios from "../helpers/axios";
import store from "../store";
import { cartConstants,ticketConstants } from "./constants";

export const getSearchedTicket = (data) => {
	return async (dispatch) => {
		try {
			dispatch({ type: ticketConstants.GET_SEARCHED_TICKET_REQUEST });
			const res = await axios.get("/ticket/getTicketByLocation",data);
			console.log(res,da);
			if (res.status === 200) {
				const { tickets } = res.data;

					dispatch({
						type: ticketConstants.GET_SEARCHED_TICKET_SUCCESS,
						payload: { tickets },
					});
				}else{
					dispatch({type:ticketConstants.GET_SEARCHED_TICKET_FAILURE})
				}

		} catch (error) {
			console.log(error);
		}
	};
};
