import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
	getAllCategory,
	getProductsBySlug,
	getSubCategory,
} from "../../../actions";
import { generatePublicUrl } from "../../../urlconfig";
import { Button } from "@material-ui/core";
import { Link } from "react-router-dom";
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import "./style.css";


const useStyles = makeStyles((theme) => ({
	root: {
		flexGrow: 1,
	},
	paper: {
		height: 140,
		width: 150,
	},
	control: {
		padding: theme.spacing(2),
	},
}));

export default function ProductStore(props) {

	const search=props.location.search
	const parentId = search.slice(5, 29);

	const [spacing, setSpacing] = React.useState(2);
	const classes = useStyles();

	const handleChange = (event) => {
		setSpacing(Number(event.target.value));
	};

	const dispatch = useDispatch();
	const product = useSelector((state) => state.product);

	const [priceRange, setPriceRange] = useState({
		under5k: "5000",
		under10k: "10000",
		under15k: "15000",
		under20k: "20000",
		under30k: "30000",
	});
	const { match } = props;
	console.log(match.params.slug);
	const category = useSelector((state) => state.category);
	const subCategories=category.subCategories
	const categories = category.categories;
	const categoriesArray = [];

	// Object.keys(categories).map((cat,index)=>{
	// 	categoriesArray.push(cat)
	// })
	// console.log(categoriesArray)
	// const selectedCategory = categories.filter((category) => {
	// 	return category.slug == match.params.slug;
	// });

	// console.log(selectedCategory);

	useEffect(() => {
		const { match } = props;
		console.log(match);

		dispatch(getAllCategory());
		dispatch(getProductsBySlug(match.params.slug));
		dispatch(getSubCategory(parentId));
	}, []);

	return (
		<>
			{subCategories ? (
				<Grid container className={classes.root} spacing={2}>
					<Grid item xs={12}>
						<Grid container justify='center' spacing={spacing}>
							{subCategories.map((category) => (
								<div className='mx-sm-1 mx-lg-4'>
									<Link
										className='categoryLink'
										to={`/${category.slug}?cid=${category._id}&type=${category.type}`}>
										<div
											className='categorybox'
											style={{
												display: "flex",
												flexDirection: "column",
											}}>
											<img
												style={{
													width: "100px",
													height: "80px",
													borderRadius: "10px",
												}}
												src='https://cdn.mos.cms.futurecdn.net/vEcELHdn998wRTcCzqV5m9.jpg'
											/>
											<h4 style={{ margin: "auto" }}>{category.name}</h4>
										</div>
									</Link>
								</div>
							))}
						</Grid>
					</Grid>
				</Grid>
			) : null}

			<Grid container className={classes.root} spacing={2}>
				<Grid item xs={12}>
					<Grid container justify='center' spacing={spacing}>
						{product.products.map((product) => (
							// <Grid
							// 	key={product}
							// 	item
							// 	className='product'
							// 	style={{
							// 		boxSizing: "border-box",
							// 		display: "flex",
							// 		flexDirection: "column",
							// 		justifyContent: "space-between",
							// 	}}>
								<Link
									className='productContainer mx-l'
									to={`/${product.slug}/${product._id}/p`}>
									<div className=''>
										{/* <Paper className={classes.paper} /> */}
										<img
											style={{ width: "150px" }}
											src='https://cdn.mos.cms.futurecdn.net/vEcELHdn998wRTcCzqV5m9.jpg'
										/>
										<p>{product.name}</p>
										<p>{product.price}</p>
										<Button variant='contained' color='primary'>
											Add To Cart
										</Button>
									</div>
								</Link>
							// </Grid>
						))}
					</Grid>
				</Grid>
			</Grid>
		</>
	);
}
