			// 	for (let category of categories) {
			// 		myCategories.push(
			// 			<li key={category.name}>
			// 				{category.parentId ? (
			// 					<a
			// 						href={`/${category.slug}?cid=${category._id}&type=${category.type}`}>
			// 						{category.name}
			// 					</a>
			// 				) : (
			// 					<span>{category.name}</span>
			// 				)}

			// 				{category.children ? (
			// 					<ul>{renderCategories(category.children)}</ul>
			// 				) : null}
			// 			</li>
			// 		);
			// 	}
			// 	return myCategories;
			// };
            const myCategories = [];
            categories.map((category,index)=>{

             category.children.map((child,index)=>{
                 myCategories.push(child)
             })

            })

            console.log(myCategories)


	return (
		// <Grid container className={classes.root} spacing={2}>
		// 	<Grid item xs={12}>
				<Slider {...settings}>
					{/* <Grid container justify='center' spacing={spacing}> */}
						{myCategories.map((category) => (
							<Grid key={category} item>
								{/* <img style={{width:'100%',height:'100%'}} src={generatePublicUrl(category.img)} /> */}
								{/* <Paper className={classes.paper}> */}
								<Link
									to={`/${category.slug}?cid=${category._id}&type=${category.type}`}
									style={{ textDecoration: "none" }}>
									<div
										className='categorybox'
										// style={{
										// 	display: "flex",
										// 	flexDirection: "column",
										// }}
										>
										<img
											style={{
												width: "100px",
												height: "80px",
												borderRadius: "10px",
											}}
											src='https://cdn.mos.cms.futurecdn.net/vEcELHdn998wRTcCzqV5m9.jpg'
										/>
										<h4 style={{ margin: "auto" }}>{category.name}</h4>
									</div>
								</Link>
								{/* </Paper> */}
							</Grid>
						))}
					{/* </Grid> */}
				</Slider>
		// 	</Grid>
		// </Grid>
	);
}
