import { cartConstants } from "../actions/constants";


const initState={
    cartItems:{
        // 123:{
        //     _id:123,
        //     name:'',
        //     img:'',
        //     price:'',
        //     qty:''
        // }

    }
}

export default (state=initState,action)=>{
    switch(action.type){
        case cartConstants.ADD_TO_CART:
            state={
                ...state,
                cartItems:action.payload.products
            }
            break
    }
    return state
}
