import React, { useState } from "react";
import { generatePublicUrl } from "../../../urlconfig";
import "./design.css";

export default function CartItem2(props) {
	const { _id, name, price, img } = props.cartItem;
	const [qty, setQty] = useState(props.cartItem.qty);

	const onQuantityIncrement = () => {
		setQty(qty + 1);
		props.onQuantityInc(_id, qty + 1);
	};

	const onQuantityDecrement = () => {
		if (qty <= 1) return;
		setQty(qty - 1);
		props.onQuantityDec(_id, qty - 1);
	};
	console.log(img);

	return (
		<tr>
			<td>
				<div class='cart-info'>
					<img style={{heu}} src={generatePublicUrl(img)} alt='' />
					<div>
						<p>{name}</p>
						<span>Price:BDT {price}</span>
						<br />
						<a href='#'>remove</a>
					</div>
				</div>
			</td>
			<td style={{ display: "flex" }}>
				<button conClick={onQuantityDecrement}> -</button>
				<input type='number' value={qty} min='1' readOnly />
				<button onClick={onQuantityIncrement}>+ </button>
			</td>
			<td>Subtotal:{qty * price}</td>
		</tr>
	);
}
