import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link, Redirect } from "react-router-dom";
import "./style.css";
import Header from "../../../components/Header";
import Noty from "noty";
import { register, logout } from "../../../actions/auth.action";
import {
	Grid,
	Paper,
	Avatar,
	Typography,
	TextField,
	Button,
} from "@material-ui/core";
import AddCircleOutlineOutlinedIcon from "@material-ui/icons/AddCircleOutlineOutlined";
import Radio from "@material-ui/core/Radio";
import RadioGroup from "@material-ui/core/RadioGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import FormControl from "@material-ui/core/FormControl";
import FormLabel from "@material-ui/core/FormLabel";
import Checkbox from "@material-ui/core/Checkbox";
const Signup = () => {
	const paperStyle = { padding: "30px 20px", width: 300, margin: "20px auto" };
	const headerStyle = { margin: 0 };
	const avatarStyle = { backgroundColor: "#1bbd7e" };
	const marginTop = { marginTop: 5 };




    	const [phone, setPhone] = useState("");
        const [gender, setGender] = useState('')
			const [name, setName] = useState("");
			const [email, setEmail] = useState("");
			const [password, setPassword] = useState("");
			const [confirmPassword, setConfirmPassword] = useState("");
			const dispatch = useDispatch();

			const auth = useSelector((state) => state.auth);

			const userRegister = () => {
				if (password != confirmPassword) {
					return new Noty({
						text: "Password Didn't match ",
					}).show();
				}
				dispatch(register({ name, email, phone, password }));
			};

	useEffect(() => {
		if (auth.authenticate) {
			if (auth.authenticate) return <Redirect to='/' />;
		}
	}, [auth.authenticate]);

	if (auth.authenticate) return <Redirect to='/' />;
	return (
		<Grid>
			<Paper elevation={20} style={paperStyle}>
				<Grid align='center'>
					<Avatar style={avatarStyle}>
						<AddCircleOutlineOutlinedIcon />
					</Avatar>
					<h2 style={headerStyle}>Sign Up</h2>
					<Typography variant='caption' gutterBottom>
						<h5>Please fill this form to create an account !</h5>
					</Typography>
				</Grid>
				<form>
					<TextField
						fullWidth
						label='Name'
						placeholder='Enter your name'
						required
						onChange={(e) => setName(e.target.value)}
					/>
					<TextField
						fullWidth
						label='Email'
						placeholder='Enter your email'
						required
					/>
					<TextField
						fullWidth
						label='Phone Number'
						placeholder='Enter your phone'
						required
						onChange={(e) => setPhone(e.target.value)}
					/>
					<FormControl component='fieldset' style={marginTop}>
						<FormLabel component='legend'>Gender</FormLabel>
						<RadioGroup
							aria-label='gender'
							name='gender'
							style={{ display: "initial" }}>
							<FormControlLabel
								value='female'
								control={<Radio />}
								label='Female'
								onChange={(e) => setGender(e.target.value)}
							/>
							<FormControlLabel
								value='male'
								control={<Radio />}
								label='Male'
								onChange={(e) => setGender(e.target.value)}
							/>
						</RadioGroup>
					</FormControl>

					<TextField
						fullWidth
						label='Password'
						placeholder='Enter your password'
						onChange={(e) => setPassword(e.target.value)}
					/>
					<TextField
						fullWidth
						label='Confirm Password'
						placeholder='Confirm your password'
						onChange={(e) => setConfirmPassword(e.target.value)}
					/>
					<FormControlLabel
						control={<Checkbox name='checkedA' />}
						label='I accept the terms and conditions.'
					/>
					<Button
						type='submit'
						variant='contained'
						fullWidth
						style={{ backgroundColor: "yellow" }}
						onClick={userRegister}>
						<h5>Register</h5>
					</Button>
					<Typography variant='caption' gutterBottom>
						<h5 style={{marginTop}}>
							Already have an account? <Link to='/login' style={{color:'yellow'}}>Login</Link>
						</h5>
					</Typography>
				</form>
			</Paper>
		</Grid>
	);
};

export default Signup;
