import React from 'react'
import './style2.css'

export default function OrderDetailsPage(pr) {

    	const dispatch = useDispatch();
			const orderDetails = useSelector((state) => state.user.orderDetails);
			const orderId = props.match.params.orderId;

			useEffect(() => {
				// console.log({ props });
				const payload = {
					orderId: orderId,
				};
				dispatch(getOrder(payload));
			}, []);

			const formatDate = (date) => {
				if (date) {
					const d = new Date(date);
					return `${d.getFullYear()}-${d.getMonth() + 1}-${d.getDate()}`;
				}
				return "";
			};

			const formatDate2 = (date) => {
				const month = [
					"Jan",
					"Feb",
					"Mar",
					"Apr",
					"May",
					"June",
					"July",
					"Aug",
					"Sep",
					"Oct",
					"Nov",
					"Dec",
				];
				if (date) {
					const d = new Date(date);
					return `${month[d.getMonth()]} ${d.getDate()}, ${d.getFullYear()}`;
				}
			};

			if (!(orderDetails && orderDetails.address)) {
				return null;
			}
    return (
       <div class="container">
    <article class="card">
        <header class="card-header"> My Orders / Tracking </header>
        <div class="card-body">
            <h6>Order ID: OD45345345435</h6>
            <article class="card">
                <div class="card-body row">
                    <div class="col"> <strong>Estimated Delivery time:</strong> <bd/>29 nov 2019 </div>
                    <div class="col"> <strong>Shipping BY:</strong> <bd/> BLUEDART, | <i class="fa fa-phone"></i> +1598675986 </div>
                    <div class="col"> <strong>Status:</strong> <bd/> Picked by the courier </div>
                    <div class="col"> <strong>Tracking #:</strong> <bd/> BD045903594059 </div>
                </div>
            </article>
            <div class="track">
                <div class="step active"> <span class="icon"> <i class="fa fa-check"></i> </span> <span class="text">Order confirmed</span> </div>
                <div class="step active"> <span class="icon"> <i class="fa fa-user"></i> </span> <span class="text"> Picked by courier</span> </div>
                <div class="step"> <span class="icon"> <i class="fa fa-truck"></i> </span> <span class="text"> On the way </span> </div>
                <div class="step"> <span class="icon"> <i class="fa fa-box"></i> </span> <span class="text">Ready for pickup</span> </div>
            </div>
            <hr/>
            <ul class="row">
                <li class="col-md-4">
                    <figure class="itemside mb-3">
                        <div class="aside"><img src="https://res.cloudinary.com/dxfq3iotg/image/upload/v1571751108/Ecommerce/laptop-dell-xps-15-computer-monitors-laptops.jpg" class="img-sm border"/></div>
                        <figcaption class="info align-self-center">
                            <p class="title">Dell Laptop with 500GB HDD <bd/> 8GB RAM</p> <span class="text-muted">$950 </span>
                        </figcaption>
                    </figure>
                </li>
                <li class="col-md-4">
                    <figure class="itemside mb-3">
                        <div class="aside"><img src="https://res.cloudinary.com/dxfq3iotg/image/upload/v1571750967/Ecommerce/ef192a21ec96.jpg" class="img-sm border"/></div>
                        <figcaption class="info align-self-center">
                            <p class="title">HP Laptop with 500GB HDD <bd/> 8GB RAM</p> <span class="text-muted">$850 </span>
                        </figcaption>
                    </figure>
                </li>
                <li class="col-md-4">
                    <figure class="itemside mb-3">
                        <div class="aside"><img src="https://res.cloudinary.com/dxfq3iotg/image/upload/v1571750722/Ecommerce/acer-v-17-nitro-realsense.jpg" class="img-sm border"/></div>
                        <figcaption class="info align-self-center">
                            <p class="title">ACER Laptop with 500GB HDD <bd/> 8GB RAM</p> <span class="text-muted">$650 </span>
                        </figcaption>
                    </figure>
                </li>
            </ul>
            <hr/>
            <a href="#" class="btn btn-warning" data-abc="true"> <i class="fa fa-chevron-left"></i> Back to orders</a>
        </div>
    </article>
</div>
    )
}
