import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import CssBaseline from "@material-ui/core/CssBaseline";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Paper from "@material-ui/core/Paper";
import Stepper from "@material-ui/core/Stepper";
import Step from "@material-ui/core/Step";
import StepLabel from "@material-ui/core/StepLabel";
import Link from "@material-ui/core/Link";
import AddressForm from "./AddressForm";
import PaymentForm from "./PaymentForm";
import Review from "./Review";
import { useSelector, useDispatch } from "react-redux";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import TextField from "@material-ui/core/TextField";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Checkbox from "@material-ui/core/Checkbox";
import { addAddress } from "../../../actions/user.action";
import "./style.css";
import Swal from "sweetalert2";
import { Button, SwipeableDrawer } from "@material-ui/core";
import "./style.css";

function Copyright() {
	return (
		<Typography variant='body2' color='textSecondary' align='center'>
			{"Copyright Â© "}
			<Link color='inherit' href='https://material-ui.com/'>
				Your Website
			</Link>{" "}
			{new Date().getFullYear()}
			{"."}
		</Typography>
	);
}

const useStyles = makeStyles((theme) => ({
	appBar: {
		position: "relative",
	},
	layout: {
		width: "auto",
		fontSize: "1.8 rem",
		marginLeft: theme.spacing(2),
		marginRight: theme.spacing(2),
		[theme.breakpoints.up(600 + theme.spacing(2) * 2)]: {
			width: 600,
			marginLeft: "auto",
			marginRight: "auto",
		},
	},
	paper: {
		marginTop: theme.spacing(3),
		marginBottom: theme.spacing(3),
		padding: theme.spacing(2),
		[theme.breakpoints.up(600 + theme.spacing(3) * 2)]: {
			marginTop: theme.spacing(6),
			marginBottom: theme.spacing(6),
			padding: theme.spacing(3),
		},
	},
	stepper: {
		padding: theme.spacing(3, 0, 5),
	},
	buttons: {
		display: "flex",
		justifyContent: "flex-end",
	},
	button: {
		marginTop: theme.spacing(3),
		marginLeft: theme.spacing(1),
	},
}));

const steps = ["Shipping address", "Payment details", "Review your order"];
const renderAddressForm = () => {
	return (
		<React.Fragment>
			<div className='mainContainer' style={{ fontSize: "1.8rem" }}>
				<Typography variant='h6' gutterBottom>
					<h4>Shipping address</h4>
				</Typography>
				<Grid container spacing={3}>
					<Grid item xs={12} sm={6}>
						<TextField
							required
							id='firstName'
							name='Full Name'
							label='Full name'
							onChange={(e) => setName(e.target.value)}
							fullWidth
							autoComplete='given-name'
						/>
					</Grid>
					{/* <Grid item xs={12} sm={6}>
						<TextField
							required
							id='lastName'
							name='lastName'
							label='Last name'
							fullWidth
							autoComplete='family-name'
						/>
					</Grid> */}
					<Grid item xs={12}>
						<TextField
							required
							id='address1'
							name='address1'
							label='Address line 1'
							fullWidth
							onChange={(e) => setAddress(e.target.value)}
							autoComplete='shipping address-line1'
						/>
					</Grid>
					<Grid item xs={12}>
						<TextField
							id='address2'
							name='address2'
							label='Address line 2 (optiona)'
							fullWidth
							autoComplete='shipping address-line2'
						/>
					</Grid>
					<Grid item xs={12} sm={6}>
						<TextField
							required
							id='mobileNumber'
							name='mobileNumber'
							label='Mobile Number'
							onChange={(e) => setMobileNumber(e.target.value)}
							fullWidth
							autoComplete='mobileNumber'
						/>
					</Grid>
					<Grid item xs={12} sm={6}>
						<TextField
							id='state'
							name='state'
							label='City/District'
							onChange={(e) => setCityDistrict(e.target.value)}
							fullWidth
						/>
					</Grid>
					<Grid item xs={12} sm={6}>
						<TextField
							required
							id='zip'
							name='zip'
							label='Zip / Postal code'
							onChange={(e) => setZipCode(e.target.value)}
							fullWidth
							autoComplete='shipping postal-code'
						/>
					</Grid>
					<Grid item xs={12} sm={6}>
						<TextField
							required
							id='alternatePhone'
							name='alternatePhone'
							label='Secondery Phone'
							onChange={(e) => setAlternatePhone(e.target.value)}
							fullWidth
						/>
					</Grid>
					<Grid item xs={12}>
						<FormControlLabel
							control={
								<Checkbox color='secondary' name='saveAddress' value='yes' />
							}
							label='Use this address for payment details'
						/>
					</Grid>
				</Grid>
				<Button
					fullWidth
					style={{ backgroundColor: "yellow", color: "gray" }}
					onClick={onAddressSubmit}>
					<h5>Submit Address</h5>
				</Button>
			</div>
		</React.Fragment>
	);
};
function getStepContent(step) {
	switch (step) {
		case 0:
			return renderAddressForm();
		case 1:
			return <PaymentForm />;
		case 2:
			return <Review />;
		default:
			throw new Error("Unknown step");
	}
}

export default function Checkout(props) {
	const classes = useStyles();
	const [activeStep, setActiveStep] = React.useState(0);

	const handleNext = () => {
		setActiveStep(activeStep + 1);
	};

	const handleBack = () => {
		setActiveStep(activeStep - 1);
	};



	// Config for Address form

		const { initialData, onCancel } = props;
		const [name, setName] = useState(initialData ? initialData.name : "");
		const [mobileNumber, setMobileNumber] = useState(
			initialData ? initialData.mobileNumber : ""
		);
		const [zipCode, setZipCode] = useState(
			initialData ? initialData.pinCode : ""
		);
		const [locality, setLocality] = useState(
			initialData ? initialData.locality : ""
		);
		const [address, setAddress] = useState(
			initialData ? initialData.address : ""
		);
		const [cityDistrict, setCityDistrict] = useState(
			initialData ? initialData.cityDistrictTown : ""
		);
		const [state, setState] = useState(initialData ? initialData.state : "");
		const [landmark, setLandmark] = useState(
			initialData ? initialData.landmark : ""
		);
		const [alternatePhone, setAlternatePhone] = useState(
			initialData ? initialData.alternatePhone : ""
		);
