import React, { useState,useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import CssBaseline from "@material-ui/core/CssBaseline";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Paper from "@material-ui/core/Paper";
import Stepper from "@material-ui/core/Stepper";
import Step from "@material-ui/core/Step";
import StepLabel from "@material-ui/core/StepLabel";
import Link from "@material-ui/core/Link";
import AddressForm2 from "./AddressForm";
import PaymentForm2 from "./PaymentForm";
import Review from "./Review";
import { useSelector, useDispatch } from "react-redux";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import TextField from "@material-ui/core/TextField";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Checkbox from "@material-ui/core/Checkbox";
import Radio from "@material-ui/core/Radio";
import RadioGroup from "@material-ui/core/RadioGroup";
import FormControl from "@material-ui/core/FormControl";
import FormLabel from "@material-ui/core/FormLabel";
import { addAddress,getAddress } from "../../../actions/user.action";
import "./style.css";
import Swal from "sweetalert2";
import { Button, SwipeableDrawer } from "@material-ui/core";
import "./style.css";

function Copyright() {
	return (
		<Typography variant='body2' color='textSecondary' align='center'>
			{"Copyright Â© "}
			<Link color='inherit' href='https://material-ui.com/'>
				Your Website
			</Link>{" "}
			{new Date().getFullYear()}
			{"."}
		</Typography>
	);
}

const useStyles = makeStyles((theme) => ({
	appBar: {
		position: "relative",
	},
	layout: {
		width: "auto",
		fontSize: "1.8 rem",
		marginLeft: theme.spacing(2),
		marginRight: theme.spacing(2),
		[theme.breakpoints.up(600 + theme.spacing(2) * 2)]: {
			width: 600,
			marginLeft: "auto",
			marginRight: "auto",
		},
	},
	paper: {
		marginTop: theme.spacing(3),
		marginBottom: theme.spacing(3),
		padding: theme.spacing(2),
		[theme.breakpoints.up(600 + theme.spacing(3) * 2)]: {
			marginTop: theme.spacing(6),
			marginBottom: theme.spacing(6),
			padding: theme.spacing(3),
		},
	},
	stepper: {
		padding: theme.spacing(3, 0, 5),
	},
	buttons: {
		display: "flex",
		justifyContent: "flex-end",
	},
	button: {
		marginTop: theme.spacing(3),
		marginLeft: theme.spacing(1),
	},
}));




 function AddressForm(props) {
	 const user = useSelector((state) => state.user);
		const previousAddress = user.address;
		const oldAddress = user.address.slice(user.address.length - 1)[0];
		console.log(oldAddress)
	  useEffect(() => {
			dispatch(getAddress());
		}, []);
	 const { initialData, onCancel } = props;
		const [name, setName] = useState(oldAddress ? oldAddress.name : "");
		const [mobileNumber, setMobileNumber] = useState(
			oldAddress ? oldAddress.mobileNumber : ""
		);
		const [zipCode, setZipCode] = useState(
			oldAddress ? oldAddress.zipCode : ""
		);

		const [address, setAddress] = useState(
			oldAddress ? oldAddress.address : ""
		);
		const [cityDistrict, setCityDistrict] = useState(
			oldAddress ? oldAddress.cityDistrict : ""
		);

		const [alternatePhone, setAlternatePhone] = useState(
			oldAddress ? oldAddress.alternatePhone : ""
		);
		const dispatch = useDispatch();

		console.log(name)


			const [selectedAddress, setSelectedAddress] = useState('');
		const [id, setId] = useState(initialData ? initialData._id : "");


		const onAddressSubmit = (e) => {
			const payload = {
				address: {
					name,
					mobileNumber,
					zipCode,
					address,
					cityDistrict,
					alternatePhone,
				},
			};
			console.log(payload);
			if (id) {
				payload.address._id = id;
			}
			if (name || mobileNumber || zipCode || address || cityDistrict == "") {
				console.log(payload)
				// return Swal.fire({
				// 	icon: "error",
				// 	title: "Oops...",
				// 	text: "Submit form correctly..!",
				// });
				console.log(selectedAddress)
			}

			dispatch(addAddress(payload));
		};
	return (
		<React.Fragment>
			<div className='mainContainer' style={{ fontSize: "1.8rem" }}>
				<Typography variant='h6' gutterBottom>
					<h4>Shipping address</h4>
				</Typography>

				<p>{previousAddress.address}</p>

				<Grid container spacing={3}>
					<div>
						<select name='cars'  id='cars'>
							{user.address.map((addr)=>{

	                        <option value={addr.address}>{addr.address}</option>;
							console.log(addr)
							})}


						</select>
					</div>
					<Grid item xs={12} sm={6}>
						<TextField
							required
							id='firstName'
							name='Full Name'
							label='Full name'
							value={name}
