import styled from "styled-components";

export const HomeWrapper = styled.div`
	.slick-arrow {
		background-color: red;
		height: 35px;
		width: 35px;
		border-radius: 100px;
	}
	.slick-arrow:hover,
	.slick-arrow:active,
	.slick-arrow:focus {
		background-color: red !important;
	}
`;
